function setIndexLanguage(lang) {
  const t = translationsIndex[lang] || translationsIndex['it'];
  // Benvenuto
  const welcome = document.querySelector('.welcome');
  if (welcome) welcome.innerHTML = t.welcome;
  const welcomeDesc = document.querySelector('.welcome-desc');
  if (welcomeDesc) welcomeDesc.innerHTML = t.welcomeDesc;
  const whatsapp = document.querySelector('.whatsapp');
  if (whatsapp) whatsapp.innerHTML = t.whatsappBtn;

  // Servizi
  const cleaningTitle = document.querySelector('.cleaningTitle');
  if (cleaningTitle) cleaningTitle.innerHTML = t.cleaningTitle;
  const cleaningDesc = document.querySelector('.cleaningDesc');
  if (cleaningDesc) cleaningDesc.innerHTML = t.cleaningDesc;

  const shiftsTitle = document.querySelector('.shiftsTitle');
  if (shiftsTitle) shiftsTitle.innerHTML = t.shiftsTitle;
  const shiftsDesc = document.querySelector('.shiftsDesc');
  if (shiftsDesc) shiftsDesc.innerHTML = t.shiftsDesc;

  const domoticsTitle = document.querySelector('.domoticsTitle');
  if (domoticsTitle) domoticsTitle.innerHTML = t.domoticsTitle;
  const domoticsDesc = document.querySelector('.domoticsDesc');
  if (domoticsDesc) domoticsDesc.innerHTML = t.domoticsDesc;

  const securityTitle = document.querySelector('.securityTitle');
  if (securityTitle) securityTitle.innerHTML = t.securityTitle;
  const securityDesc = document.querySelector('.securityDesc');
  if (securityDesc) securityDesc.innerHTML = t.securityDesc;

  const mysteryTitle = document.querySelector('.mysteryTitle');
  if (mysteryTitle) mysteryTitle.innerHTML = t.mysteryTitle;
  const mysteryDesc = document.querySelector('.mysteryDesc');
  if (mysteryDesc) mysteryDesc.innerHTML = t.mysteryDesc;

  const aiTitle = document.querySelector('.aiTitle');
  if (aiTitle) aiTitle.innerHTML = t.aiTitle;
  const aiDesc = document.querySelector('.aiDesc');
  if (aiDesc) aiDesc.innerHTML = t.aiDesc;
}

window.setIndexLanguage = setIndexLanguage;
